import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import './Treaties.css';
import './Empires.css';
import './Boards.css';
import './Accounts.css';

function getNationPairs(nations) {
    const pairs = [];
    for (let i = 0; i < nations.length; i++) {
        for (let j = i + 1; j < nations.length; j++) {
            pairs.push([nations[i], nations[j]]);
        }
    }
    return pairs;
}

function App() {
    // Remove unused empireInput, selectedNation, author
    const [empires, setEmpires] = useState([]);
    const [selected, setSelected] = useState(() => {
        const saved = localStorage.getItem('stellarisSelectedBoard');
        return saved ? saved : null;
    });
    const [messages, setMessages] = useState({});
    const [text, setText] = useState('');
    const pollingRef = useRef();
    const messagesEndRef = useRef(null);
    const [accounts, setAccounts] = useState({});
    const [accountsLoaded, setAccountsLoaded] = useState(false);
    const [account, setAccount] = useState(() => {
        const saved = localStorage.getItem('stellarisAccount');
        return saved ? JSON.parse(saved) : null;
    });
    const [loginUser, setLoginUser] = useState('');
    const [loginPass, setLoginPass] = useState('');
    const [error, setError] = useState('');
    const [activeTab, setActiveTab] = useState('channels');
    const [empirePage, setEmpirePage] = useState(null);
    const [editEmpire, setEditEmpire] = useState(null);
    const [empireInfo, setEmpireInfo] = useState({});
    const [empireInfoLoaded, setEmpireInfoLoaded] = useState(false);

    // Account creation and management
    const [newAccountName, setNewAccountName] = useState('');
    const [newAccountPass, setNewAccountPass] = useState('');
    const [accountError, setAccountError] = useState('');
    const [accountSuccess, setAccountSuccess] = useState('');
    const [accountsTabLoading, setAccountsTabLoading] = useState(false);
    const [editAccount, setEditAccount] = useState(null);
    const [editAccountName, setEditAccountName] = useState('');
    const [editAccountPass, setEditAccountPass] = useState('');
    const [editAccountError, setEditAccountError] = useState('');
    const [editAccountLoading, setEditAccountLoading] = useState(false);

    // Manage Empires tab
    const [newEmpireName, setNewEmpireName] = useState('');
    const [newEmpireError, setNewEmpireError] = useState('');
    const [newEmpireLoading, setNewEmpireLoading] = useState(false);

    // --- Treaties State (NEW IMPLEMENTATION) ---
    const [treaties, setTreaties] = useState([]);
    const [treatiesLoaded, setTreatiesLoaded] = useState(false);
    const [treatyDialog, setTreatyDialog] = useState({ open: false, mode: null, data: null });
    const [treatyError, setTreatyError] = useState('');
    const [treatySaving, setTreatySaving] = useState(false);

    // --- Fetch treaties ---
    function fetchTreaties() {
        fetch('/treaties.json?ts=' + Date.now())
            .then(res => res.json())
            .then(data => {
                setTreaties(Array.isArray(data) ? data : []);
                setTreatiesLoaded(true);
            })
            .catch(() => setTreatiesLoaded(true));
    }
    useEffect(() => { fetchTreaties(); }, []);

    // --- Treaty CRUD ---
    function openTreatyDialog(mode, data = null) {
        setTreatyDialog({ open: true, mode, data });
        setTreatyError('');
    }
    function closeTreatyDialog() {
        setTreatyDialog({ open: false, mode: null, data: null });
        setTreatyError('');
    }
    function handleTreatySave(form) {
        setTreatySaving(true);
        setTreatyError('');
        const payload = { ...form };
        if (!payload.title || !payload.content || !payload.participants?.length) {
            setTreatyError('Title, content, and at least one participant are required.');
            setTreatySaving(false);
            return;
        }
        if (!payload.owner) payload.owner = account?.username || '';
        if (!payload.status) payload.status = 'discussion';
        if (!payload.id) payload.id = Date.now().toString();

        fetch('/api/treaties', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(payload)
        })
        .then(res => res.json())
        .then(data => {
            if (data.success) {
                // --- FIX: update treaties state locally, just like accounts ---
                setTreaties(prev => {
                    // If editing, replace; if new, add
                    const idx = prev.findIndex(t => t.id === payload.id);
                    if (idx !== -1) {
                        const updated = [...prev];
                        updated[idx] = { ...prev[idx], ...payload };
                        return updated;
                    } else {
                        return [...prev, payload];
                    }
                });
                closeTreatyDialog();
            } else {
                setTreatyError(data.error || 'Failed to save treaty');
            }
        })
        .catch(() => setTreatyError('Failed to save treaty'))
        .finally(() => setTreatySaving(false));
    }
    function handleTreatyTransferOwner(id, newOwner) {
        setTreatySaving(true);
        fetch('/api/treaties/transfer', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ id, newOwner })
        })
        .then(res => res.json())
        .then(data => {
            if (data.success) {
                setTreaties(prev => prev.map(t => t.id === id ? { ...t, owner: newOwner } : t));
                closeTreatyDialog();
            }
        })
        .finally(() => setTreatySaving(false));
    }

    // --- Treaty Permissions ---
    function canEditTreaty(treaty) {
        if (!account) return false;
        return account.username === treaty.owner || account.username === "GameMaster";
    }
    function canTransferTreaty(treaty) {
        return account && account.username === "GameMaster";
    }

    // --- Treaty Dialog UI ---
    function TreatyDialog({ open, mode, data, onSave, onClose, error, saving, empires, accounts }) {
        const [form, setForm] = useState(() => data ? { ...data } : {
            title: '', content: '', participants: [], status: 'discussion', owner: account?.username || ''
        });
        const [showParticipantsDropdown, setShowParticipantsDropdown] = useState(false);
        const dropdownBtnRef = useRef(null);
        const dropdownListRef = useRef(null);

        useEffect(() => {
            if (data) setForm({ ...data });
            else setForm({ title: '', content: '', participants: [], status: 'discussion', owner: account?.username || '' });
        }, [data, open]);

        // --- Dropdown close on outside click ---
        useEffect(() => {
            if (!showParticipantsDropdown) return;
            function handleClick(e) {
                if (
                    dropdownBtnRef.current &&
                    !dropdownBtnRef.current.contains(e.target) &&
                    dropdownListRef.current &&
                    !dropdownListRef.current.contains(e.target)
                ) {
                    setShowParticipantsDropdown(false);
                }
            }
            document.addEventListener('mousedown', handleClick);
            return () => document.removeEventListener('mousedown', handleClick);
        }, [showParticipantsDropdown]);

        // Keyboard navigation: close dropdown on Escape
        useEffect(() => {
            if (!showParticipantsDropdown) return;
            function handleKey(e) {
                if (e.key === "Escape") setShowParticipantsDropdown(false);
            }
            document.addEventListener('keydown', handleKey);
            return () => document.removeEventListener('keydown', handleKey);
        }, [showParticipantsDropdown]);

        if (!open) return null;

        const isEdit = mode === 'edit';
        const statuses = [
            { value: 'discussion', label: 'ðŸ—¨ï¸ In Discussion' },
            { value: 'active', label: 'âœ… Active' },
            { value: 'broken', label: 'âŒ Broken' },
            { value: 'expired', label: 'âŒ› Expired' },
            { value: 'other', label: 'â“ Other' }
        ];

        // Helper for participants dropdown
        function toggleParticipant(name) {
            setForm(f => {
                const exists = f.participants.includes(name);
                return {
                    ...f,
                    participants: exists
                        ? f.participants.filter(p => p !== name)
                        : [...f.participants, name]
                };
            });
        }

        return (
            <div className="treaty-dialog-overlay">
                <div className="treaty-dialog">
                    <h2>{isEdit ? 'Edit Treaty' : 'New Treaty'}</h2>
                    <form onSubmit={e => { e.preventDefault(); onSave(form); }}>
                        <label>
                            Title:
                            <input
                                value={form.title}
                                onChange={e => setForm(f => ({ ...f, title: e.target.value }))}
                                required
                                disabled={saving}
                            />
                        </label>
                        <label>
                            Status:
                            <select
                                value={form.status}
                                onChange={e => setForm(f => ({ ...f, status: e.target.value }))}
                                disabled={saving}
                            >
                                {statuses.map(s => (
                                    <option key={s.value} value={s.value}>{s.label}</option>
                                ))}
                            </select>
                        </label>
                        <label style={{ position: 'relative' }}>
                            Participants:
                            <div
                                className="participants-dropdown"
                                ref={dropdownBtnRef}
                                tabIndex={0}
                                style={{
                                    userSelect: 'none',
                                    background: 'var(--primary-bg)',
                                    border: '1px solid var(--border)',
                                    borderRadius: 8,
                                    padding: '0.7rem 1rem',
                                    minHeight: '2.2rem',
                                    cursor: saving ? 'not-allowed' : 'pointer',
                                    marginBottom: '0.7rem',
                                    position: 'relative',
                                    display: 'flex',
                                    alignItems: 'center',
                                    justifyContent: 'space-between'
                                }}
                                // Prevent the first click from toggling the first item
                                onMouseDown={e => e.preventDefault()}
                                onClick={e => {
                                    if (!saving) setShowParticipantsDropdown(v => !v);
                                }}
                                aria-haspopup="listbox"
                                aria-expanded={showParticipantsDropdown}
                            >
                                <span style={{
                                    color: form.participants.length ? 'var(--text)' : '#aaa',
                                    overflow: 'hidden',
                                    textOverflow: 'ellipsis',
                                    whiteSpace: 'nowrap',
                                    flex: 1
                                }}>
                                    {form.participants.length
                                        ? form.participants.join(', ')
                                        : 'Select participants...'}
                                </span>
                                <span style={{ marginLeft: 8, color: '#aaa', fontSize: '1.1em', pointerEvents: 'none' }}>â–¼</span>
                            </div>
                            {showParticipantsDropdown && (
                                <div
                                    className="participants-dropdown-list"
                                    ref={dropdownListRef}
                                    style={{
                                        position: 'absolute',
                                        left: 0,
                                        right: 0,
                                        top: 'calc(100% + 8px)',
                                        zIndex: 1001,
                                        background: 'var(--card-bg)',
                                        border: '1.5px solid var(--accent)',
                                        borderRadius: 10,
                                        boxShadow: '0 4px 24px #0005',
                                        minWidth: 210,
                                        padding: '0.3rem 0',
                                        display: 'flex',
                                        flexDirection: 'column',
                                        gap: '0.2rem',
                                        maxHeight: 220,
                                        overflowY: 'auto'
                                    }}
                                    onClick={e => e.stopPropagation()}
                                    role="listbox"
                                >
                                    {empires.map((e, idx) => (
                                        <button
                                            key={e}
                                            type="button"
                                            className="participants-dropdown-item"
                                            style={{
                                                background: 'none',
                                                border: 'none',
                                                color: 'var(--accent)',
                                                fontSize: '1.08rem',
                                                textAlign: 'left',
                                                padding: '0.7rem 1.2rem',
                                                cursor: saving ? 'not-allowed' : 'pointer',
                                                borderRadius: 7,
                                                transition: 'background 0.15s, color 0.15s',
                                                width: '100%',
                                                display: 'flex',
                                                alignItems: 'center',
                                                gap: '0.7em',
                                                position: 'relative',
                                                backgroundColor: form.participants.includes(e) ? 'rgba(0,191,255,0.10)' : 'transparent'
                                            }}
                                            tabIndex={-1}
                                            // Prevent the first click after opening from toggling the first item
                                            onMouseDown={ev => ev.preventDefault()}
                                            onClick={ev => {
                                                ev.stopPropagation();
                                                if (!saving) toggleParticipant(e);
                                            }}
                                        >
                                            <span style={{
                                                display: 'inline-flex',
                                                alignItems: 'center',
                                                justifyContent: 'center',
                                                width: 22,
                                                height: 22,
                                                borderRadius: 5,
                                                border: '1.5px solid var(--accent)',
                                                background: form.participants.includes(e) ? 'var(--accent)' : 'var(--primary-bg)',
                                                marginRight: 10,
                                                transition: 'background 0.15s, border 0.15s'
                                            }}>
                                                <input
                                                    type="checkbox"
                                                    checked={form.participants.includes(e)}
                                                    readOnly
                                                    tabIndex={-1}
                                                    style={{
                                                        accentColor: 'var(--accent)',
                                                        width: 18,
                                                        height: 18,
                                                        margin: 0,
                                                        pointerEvents: 'none'
                                                    }}
                                                />
                                            </span>
                                            <span style={{
                                                color: form.participants.includes(e) ? '#fff' : 'var(--accent)',
                                                fontWeight: form.participants.includes(e) ? 600 : 400,
                                                flex: 1,
                                                textAlign: 'left'
                                            }}>
                                                {e}
                                            </span>
                                        </button>
                                    ))}
                                </div>
                            )}
                        </label>
                        {account?.username === "GameMaster" && (
                            <label>
                                Owner:
                                <select
                                    value={form.owner}
                                    onChange={e => setForm(f => ({ ...f, owner: e.target.value }))}
                                    disabled={saving}
                                >
                                    {Object.keys(accounts).map(acc => (
                                        <option key={acc} value={acc}>{acc}</option>
                                    ))}
                                </select>
                            </label>
                        )}
                        <label>
                            Content:
                            <textarea
                                value={form.content}
                                onChange={e => setForm(f => ({ ...f, content: e.target.value }))}
                                rows={7}
                                required
                                disabled={saving}
                            />
                        </label>
                        {error && <div className="login-error treaty-error">{error}</div>}
                        <div className="treaty-form-actions">
                            <button type="submit" className="empire-save-btn" disabled={saving}>
                                {isEdit ? "Save Changes" : "Create Treaty"}
                            </button>
                            <button type="button" className="empire-back-btn" onClick={onClose} disabled={saving}>Cancel</button>
                        </div>
                    </form>
                </div>
            </div>
        );
    }

    // --- Treaty View UI ---
    function TreatyView({ treaty, onBack, onEdit, onTransfer, canEdit, canTransfer, accounts }) {
        if (!treaty) return null;
        const statuses = {
            discussion: 'ðŸ—¨ï¸ In Discussion',
            active: 'âœ… Active',
            broken: 'âŒ Broken',
            expired: 'âŒ› Expired',
            other: 'â“ Other'
        };
        return (
            <div className="treaty-dialog-overlay">
                <div className="treaty-dialog">
                    <h2>{treaty.title}</h2>
                    <div><b>Status:</b> {statuses[treaty.status] || treaty.status}</div>
                    <div><b>Owner:</b> {treaty.owner}</div>
                    <div><b>Participants:</b> {treaty.participants?.join(', ') || <i>None</i>}</div>
                    <div style={{margin: '1em 0', whiteSpace: 'pre-line'}}>{treaty.content}</div>
                    <div className="treaty-form-actions">
                        <button className="empire-back-btn" style={{minWidth: 110, maxWidth: 160}} onClick={onBack}>Back</button>
                        {canEdit && <button className="empire-save-btn" style={{minWidth: 110, maxWidth: 160}} onClick={onEdit}>Edit</button>}
                        {/* The owner transfer dropdown is intentionally removed from view mode */}
                    </div>
                </div>
            </div>
        );
    }

    // --- NEW: Permissions state for GameMaster ---
    const [showMenu, setShowMenu] = useState(false);
    const [showPermissions, setShowPermissions] = useState(false);
    const [gmPermissions, setGmPermissions] = useState(() => {
        // Persist in localStorage
        const saved = localStorage.getItem('stellarisGmPermissions');
        return saved ? JSON.parse(saved) : { canDeleteMessages: true };
    });

    // Save GM permissions to localStorage
    useEffect(() => {
        localStorage.setItem('stellarisGmPermissions', JSON.stringify(gmPermissions));
    }, [gmPermissions]);

    // --- NEW: Menu handlers ---
    function handleMenuToggle() {
        setShowMenu(v => !v);
        setShowPermissions(false);
    }
    function handleMenuClose() {
        setShowMenu(false);
        setShowPermissions(false);
    }
    // Keep menu open when hovering either dropdown or permissions
    const menuDropdownRef = useRef(null);
    const permissionsDropdownRef = useRef(null);

    // Close menu only if mouse leaves both dropdowns
    useEffect(() => {
        if (!showMenu && !showPermissions) return;
        function handleMouseMove(e) {
            if (
                menuDropdownRef.current &&
                permissionsDropdownRef.current &&
                !menuDropdownRef.current.contains(e.target) &&
                !permissionsDropdownRef.current.contains(e.target)
            ) {
                setShowMenu(false);
                setShowPermissions(false);
            }
        }
        document.addEventListener('mousedown', handleMouseMove);
        return () => document.removeEventListener('mousedown', handleMouseMove);
    }, [showMenu, showPermissions]);
    function handlePermissionsToggle() {
        setShowPermissions(v => !v);
    }
    function handlePermissionChange(key, value) {
        setGmPermissions(prev => ({ ...prev, [key]: value }));
    }

    // --- NEW: Other menu options ---
    function handleShowAbout() {
        alert("Stellaris RP Board\n\nCreated for roleplay and diplomacy.\n\nContact: admin@example.com");
        handleMenuClose();
    }
    function handleShowTheme() {
        alert("Theme switcher coming soon!");
        handleMenuClose();
    }

    // --- NEW: Theme toggle ---
    const [theme, setTheme] = useState(() => localStorage.getItem('stellarisTheme') || 'dark');
    useEffect(() => {
        document.body.setAttribute('data-theme', theme);
        localStorage.setItem('stellarisTheme', theme);
    }, [theme]);
    function handleThemeToggle() {
        setTheme(t => t === 'dark' ? 'light' : 'dark');
    }

    // Only load empireInfo when switching to the Empires tab and not viewing/editing any empire.
    // This prevents any reloads while editing or viewing an empire.
    useEffect(() => {
        if (
            activeTab === 'empires' &&
            !editEmpire &&
            !empirePage
        ) {
            // Add cache-busting query param
            fetch(`/empireInfo.json?ts=${Date.now()}`)
                .then(res => res.json())
                .then(data => {
                    setEmpireInfo(data && typeof data === 'object' ? data : {});
                    setEmpireInfoLoaded(true);
                })
                .catch(() => setEmpireInfoLoaded(true));
        }
    // Only run when switching tabs, or leaving view/edit mode
    }, [activeTab, editEmpire, empirePage]);

    // --- NEW: Search Empires ---
    const [empireSearch, setEmpireSearch] = useState('');
    function getEmpireNames() {
        const names = empires.map(e => e.name);
        if (!empireSearch.trim()) return names;
        return names.filter(n => n.toLowerCase().includes(empireSearch.trim().toLowerCase()));
    }

    // Helper: get boards from linked empires
    function getLinkedBoards() {
        const names = getEmpireNames();
        return getNationPairs(names);
    }

    // Helper: get account linked to empire
    function getEmpireAccount(empireName) {
        const emp = empires.find(e => e.name === empireName);
        return emp && emp.account ? emp.account : null;
    }

    // Only show boards for the empire assigned to the logged-in account, except GameMaster sees all
    const filteredBoards = account
        ? account.username === "GameMaster"
            ? getLinkedBoards()
            : getLinkedBoards().filter(([a, b]) =>
                getEmpireAccount(a) === account.username || getEmpireAccount(b) === account.username
            )
        : [];

    // GameMaster: link/unlink accounts to empires
    function handleLinkAccount(empireName, accountName) {
        fetch('/api/empires/link', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ empireName, accountName })
        })
        .then(res => res.json())
        .then(data => {
            if (data.success) {
                setEmpires(prev =>
                    prev.map(e => e.name === empireName ? { ...e, account: accountName } : e)
                );
            }
        });
    }
    function handleUnlinkAccount(empireName) {
        fetch('/api/empires/unlink', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ empireName })
        })
        .then(res => res.json())
        .then(data => {
            if (data.success) {
                setEmpires(prev =>
                    prev.map(e => e.name === empireName ? { ...e, account: null } : e)
                );
            }
        });
    }

    // Save selected board to localStorage
    function handleBoardSelect(pair) {
        const key = pair.join('|');
        setSelected(key);
        localStorage.setItem('stellarisSelectedBoard', key);
    }

    // Fetch messages for the selected board from the server
    useEffect(() => {
        // Only poll messages if on channels tab and not editing/viewing empire
        if (
            !selected ||
            activeTab !== 'channels' ||
            editEmpire ||
            empirePage
        ) {
            // Stop polling if not in correct state
            if (pollingRef.current) {
                clearInterval(pollingRef.current);
                pollingRef.current = null;
            }
            return;
        }

        // Use selected as key
        const key = selected;

        // Function to fetch messages
        const fetchMessages = () => {
            fetch(`/api/messages?board=${encodeURIComponent(key)}`)
                .then(res => {
                    if (!res.ok) throw new Error('Network response was not ok');
                    return res.json();
                })
                .then(data => {
                    setMessages(prev => ({
                        ...prev,
                        [key]: data
                    }));
                })
                .catch((err) => {
                    setMessages(prev => ({
                        ...prev,
                        [key]: []
                    }));
                    console.error('Failed to fetch messages:', err);
                });
        };

        // Initial fetch
        fetchMessages();

        // Set up polling
        pollingRef.current = setInterval(fetchMessages, 2000);

        // Cleanup on unmount or when selected/activeTab/editEmpire/empirePage changes
        return () => {
            if (pollingRef.current) {
                clearInterval(pollingRef.current);
                pollingRef.current = null;
            }
        };
    }, [selected, activeTab, editEmpire, empirePage]);

    // Scroll to bottom only when messages actually change for the selected board
    const prevMessagesLengthRef = useRef(0);
    useEffect(() => {
        if (!selected) return;
        const currentMessages = messages[selected] || [];
        if (currentMessages.length !== prevMessagesLengthRef.current) {
            if (messagesEndRef.current) {
                messagesEndRef.current.scrollIntoView({ behavior: 'auto' });
            }
            prevMessagesLengthRef.current = currentMessages.length;
        }
    }, [messages, selected]);

    // When posting, always use the logged-in account's empire name
    function postMessage(e) {
        e.preventDefault();
        if (!selected || !account || !text.trim()) return;
        const key = selected;
        const msg = {
            board: key,
            author: account.username,
            text,
            timestamp: Date.now()
        };
        fetch('/api/messages', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(msg)
        })
        .then(res => res.json())
        .then(() => {
            fetch(`/api/messages?board=${encodeURIComponent(key)}`)
                .then(res => res.json())
                .then(data => {
                    setMessages(prev => ({
                        ...prev,
                        [key]: data
                    }));
                });
        });
        setText('');
    }

    // Fetch accounts from server (accounts.json)
    useEffect(() => {
        fetch('/accounts.json')
            .then(res => res.json())
            .then(data => {
                setAccounts(data);
                setAccountsLoaded(true);
            })
            .catch(() => setAccountsLoaded(true));
    }, []);

    // Load empires from empires.json
    useEffect(() => {
        fetch('/empires.json')
            .then(res => res.json())
            .then(data => {
                setEmpires(Array.isArray(data) ? data : []);
            });
    }, []);

    // Fetch treaties from backend
    useEffect(() => {
        fetch('/treaties.json')
            .then(res => res.json())
            .then(data => {
                setTreaties(Array.isArray(data) ? data : []);
                setTreatiesLoaded(true);
            })
            .catch(() => setTreatiesLoaded(true));
    }, []);

    function handleLogin(e) {
        e.preventDefault();
        // Wait until accounts are loaded
        if (!accountsLoaded) {
            setError('Accounts are still loading, please wait...');
            return;
        }
        const username = loginUser.trim();
        const password = loginPass.trim();
        if (!accounts[username]) {
            setError('Invalid credentials');
            return;
        }
        if (accounts[username] !== password) {
            setError('Invalid credentials');
            return;
        }
        setAccount({ username });
        localStorage.setItem('stellarisAccount', JSON.stringify({ username }));
        setError('');
    }

    function handleLogout() {
        setAccount(null);
        localStorage.removeItem('stellarisAccount');
    }

    // Save empire info to backend and update local state
    function saveEmpireInfo(name, info) {
        return fetch('/api/empireInfo', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ name, info })
        })
        .then(res => res.json())
        .then(() => {
            // After saving, reload from backend with cache-busting param
            return fetch(`/empireInfo.json?ts=${Date.now()}`)
                .then(res => res.json())
                .then(data => {
                    setEmpireInfo(data && typeof data === 'object' ? data : {});
                });
        });
    }

    // Edit panel for empire info
    function EmpireEditPanel({ name, onCancel }) {
        // Only initialize edit state from empireInfo[name] when the component mounts
        const info = empireInfo[name] || {};
        const [edit, setEdit] = useState(info);

        // Do NOT use useEffect to update edit state from empireInfo.
        // This ensures the text fields are not reset while editing.

        function handleChange(e) {
            setEdit({ ...edit, [e.target.name]: e.target.value });
        }
        function handleSave(e) {
            e.preventDefault();
            saveEmpireInfo(name, edit).then(() => {
                onCancel();
            });
        }

        return (
            <form className="empire-edit-form" onSubmit={handleSave}>
                <label>
                    Lore:
                    <textarea
                        name="lore"
                        value={edit.lore || ''}
                        onChange={handleChange}
                        rows={3}
                    />
                </label>
                <label>
                    Stats:
                    <input name="stats" value={edit.stats || ''} onChange={handleChange} />
                </label>
                <label>
                    Ethics:
                    <input name="ethics" value={edit.ethics || ''} onChange={handleChange} />
                </label>
                <label>
                    Civics:
                    <input name="civics" value={edit.civics || ''} onChange={handleChange} />
                </label>
                <label>
                    Special Info:
                    <input name="special" value={edit.special || ''} onChange={handleChange} />
                </label>
                <div style={{display: 'flex', gap: '1rem', marginTop: '1rem'}}>
                    <button type="submit" className="empire-save-btn">Save</button>
                    <button type="button" className="empire-back-btn" onClick={onCancel}>Cancel</button>
                </div>
            </form>
        );
    }

    // View panel for empire info
    function EmpirePanel({ name, canEdit }) {
        const info = empireInfo[name] || {};
        return (
            <div
                className="empire-info-card empire-info-page"
                style={{
                    maxWidth: 420,
                    margin: '2rem auto',
                    background: 'var(--card-bg)',
                    border: '1.5px solid var(--accent)',
                    borderRadius: 14,
                    boxShadow: '0 2px 16px #0002',
                    padding: '2rem 2rem',
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'stretch',
                    gap: '1rem'
                }}
            >
                <h2 className="empire-info-title" style={{marginBottom: 0}}>{name}</h2>
                <div style={{color: '#b3cfff', fontWeight: 500, marginBottom: '1.2rem', fontSize: '1.08em', textAlign: 'center'}}>
                    Owner: <span style={{color: '#fff'}}>{getEmpireAccount(name) || <i>Unassigned</i>}</span>
                </div>
                <div className="empire-info-details">
                    <div><b>Lore:</b> <div className="empire-info-field">{info.lore || <i>No lore set.</i>}</div></div>
                    <div><b>Stats:</b> <div className="empire-info-field">{info.stats || <i>No stats set.</i>}</div></div>
                    <div><b>Ethics:</b> <div className="empire-info-field">{info.ethics || <i>No ethics set.</i>}</div></div>
                    <div><b>Civics:</b> <div className="empire-info-field">{info.civics || <i>No civics set.</i>}</div></div>
                    <div><b>Special Info:</b> <div className="empire-info-field">{info.special || <i>No special info set.</i>}</div></div>
                </div>
                <div style={{display: 'flex', gap: '1rem', marginTop: '1.5rem', justifyContent: 'center'}}>
                    <button className="empire-back-btn" onClick={() => setEmpirePage(null)}>Back to Empires</button>
                    {canEdit && (
                        <button className="empire-save-btn" onClick={() => setEditEmpire(name)}>Edit</button>
                    )}
                </div>
            </div>
        );
    }

    // Defensive: ensure empireInfo is always an object
    useEffect(() => {
        if (!empireInfo || typeof empireInfo !== 'object' || Array.isArray(empireInfo)) {
            setEmpireInfo({});
        }
    }, [empireInfo]);

    // Defensive: ensure accounts is always an object
    useEffect(() => {
        if (!accounts || typeof accounts !== 'object' || Array.isArray(accounts)) {
            setAccounts({});
        }
    }, [accounts]);

    // Add API to create new account
    function handleCreateAccount(e) {
        e.preventDefault();
        setAccountError('');
        setAccountSuccess('');
        const name = newAccountName.trim();
        const pass = newAccountPass.trim();
        if (!name || !pass) {
            setAccountError('Name and password required');
            return;
        }
        if (accounts[name]) {
            setAccountError('Account already exists');
            return;
        }
        setAccountsTabLoading(true);
        fetch('/api/accounts', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ name, pass })
        })
        .then(res => res.json())
        .then(data => {
            if (data.success) {
                setAccounts(prev => ({ ...prev, [name]: pass }));
                // Remove: setEmpires(prev => prev.includes(name) ? prev : [...prev, name]);
                setAccountSuccess('Account created');
                setNewAccountName('');
                setNewAccountPass('');
            } else {
                setAccountError(data.error || 'Failed to create account');
            }
        })
        .catch(() => setAccountError('Failed to create account'))
        .finally(() => setAccountsTabLoading(false));
    }

    // API: Rename account
    function handleRenameAccount(e) {
        e.preventDefault();
        setEditAccountError('');
        setEditAccountLoading(true);
        const oldName = editAccount;
        const newName = editAccountName.trim();
        if (!newName || newName === oldName) {
            setEditAccountError('Enter a new name');
            setEditAccountLoading(false);
            return;
        }
        fetch('/api/accounts/rename', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ oldName, newName })
        })
        .then(res => res.json())
        .then(data => {
            if (data.success) {
                // Update local state
                setAccounts(prev => {
                    const updated = { ...prev };
                    updated[newName] = updated[oldName];
                    delete updated[oldName];
                    return updated;
                });
                setEmpires(prev => prev.map(e => e === oldName ? newName : e));
                setEditAccount(null);
            } else {
                setEditAccountError(data.error || 'Rename failed');
            }
        })
        .catch(() => setEditAccountError('Rename failed'))
        .finally(() => setEditAccountLoading(false));
    }

    // API: Change password
    function handleChangePassword(e) {
        e.preventDefault();
        setEditAccountError('');
        setEditAccountLoading(true);
        const name = editAccount;
        const pass = editAccountPass.trim();
        if (!pass) {
            setEditAccountError('Enter a new password');
            setEditAccountLoading(false);
            return;
        }
        fetch('/api/accounts/password', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ name, pass })
        })
        .then(res => res.json())
        .then(data => {
            if (data.success) {
                setAccounts(prev => ({ ...prev, [name]: pass }));
                setEditAccount(null);
            } else {
                setEditAccountError(data.error || 'Password change failed');
            }
        })
        .catch(() => setEditAccountError('Password change failed'))
        .finally(() => setEditAccountLoading(false));
    }

    // API: Delete account
    function handleDeleteAccount(name) {
        if (!window.confirm(`Delete account "${name}"? This cannot be undone.`)) return;
        setEditAccountLoading(true);
        fetch('/api/accounts/delete', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ name })
        })
        .then(res => res.json())
        .then(data => {
            if (data.success) {
                setAccounts(prev => {
                    const updated = { ...prev };
                    delete updated[name];
                    return updated;
                });
                setEmpires(prev => prev.filter(e => e !== name));
                setEditAccount(null);
            } else {
                alert(data.error || 'Delete failed');
            }
        })
        .catch(() => alert('Delete failed'))
        .finally(() => setEditAccountLoading(false));
    }

    // API: Create new empire
    function handleCreateEmpire(e) {
        e.preventDefault();
        setNewEmpireError('');
        setNewEmpireLoading(true);
        const name = newEmpireName.trim();
        if (!name) {
            setNewEmpireError('Empire name required');
            setNewEmpireLoading(false);
            return;
        }
        if (empires.some(e => e.name === name)) {
            setNewEmpireError('Empire already exists');
            setNewEmpireLoading(false);
            return;
        }
        fetch('/api/empires/create', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ name })
        })
        .then(res => res.json())
        .then(data => {
            if (data.success) {
                setEmpires(prev => [...prev, { name, account: null }]);
                setNewEmpireName('');
            } else {
                setNewEmpireError(data.error || 'Failed to create empire');
            }
        })
        .catch(() => setNewEmpireError('Failed to create empire'))
        .finally(() => setNewEmpireLoading(false));
    }

    // API: Delete empire
    function handleDeleteEmpire(name) {
        if (!window.confirm(`Delete empire "${name}"? This cannot be undone.`)) return;
        setNewEmpireLoading(true);
        fetch('/api/empires/delete', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ name })
        })
        .then(res => res.json())
        .then(data => {
            if (data.success) {
                setEmpires(prev => {
                    const updated = prev.filter(e => e.name !== name);
                    // If the selected board involves the deleted empire, clear selection
                    if (selected) {
                        const [a, b] = selected.split('|');
                        if (a === name || b === name) {
                            setSelected(null);
                            localStorage.removeItem('stellarisSelectedBoard');
                        }
                    }
                    return updated;
                });
            } else {
                setNewEmpireError(data.error || 'Failed to delete empire');
            }
        })
        .catch(() => setNewEmpireError('Failed to delete empire'))
        .finally(() => setNewEmpireLoading(false));
    }

    // Treaties: permissions
    function canEditTreaty(treaty) {
        if (!account) return false;
        return account.username === treaty.owner || account.username === "GameMaster";
    }
    function canTransferTreaty(treaty) {
        return account && account.username === "GameMaster";
    }

    // Treaties: open new form
    function handleAddTreaty() {
        setTreatyDialog({
            open: true,
            mode: 'new',
            data: null
        });
        setTreatyError('');
    }

    // Treaties: open edit
    function handleEditTreaty(treaty) {
        setTreatyDialog({
            open: true,
            mode: 'edit',
            data: treaty
        });
        setTreatyError('');
    }

    // Treaties: open view
    function handleViewTreaty(treaty) {
        setTreatyDialog({
            open: true,
            mode: 'view',
            data: treaty
        });
        setTreatyError('');
    }

    // Treaties: cancel form
    function handleCancelTreatyForm() {
        setTreatyDialog({
            open: false,
            mode: null,
            data: null
        });
        setTreatyError('');
    }

    // Treaties: save (create or update)
    function handleSaveTreaty(e) {
        e.preventDefault();
        setTreatyError('');
        setTreatySaving(true);
        const isEdit = treatyEditId != null;
        const form = { ...treatyForm };
        if (!form.title.trim() || !form.content.trim() || !form.participants.length) {
            setTreatyError('Title, content, and at least one participant are required.');
            setTreatySaving(false);
            return;
        }
        if (!form.owner) form.owner = account ? account.username : '';
        if (!form.status) form.status = 'discussion';
        if (!form.id) form.id = isEdit ? treatyEditId : Date.now().toString();

        fetch('/api/treaties', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(form)
        })
        .then(res => res.json())
        .then(data => {
            if (data.success) {
                // Always fetch the latest treaties from /treaties.json after save
                return fetch('/treaties.json?ts=' + Date.now())
                    .then(res => res.json())
                    .then(data2 => {
                        setTreaties(Array.isArray(data2) ? data2 : []);
                        setTreatyForm(null);
                        setTreatyEditId(null);
                        setShowTreatyForm(false);
                        setTreatyViewId(form.id);
                    });
            } else {
                setTreatyError(data.error || 'Failed to save treaty');
            }
        })
        .catch(() => setTreatyError('Failed to save treaty'))
        .finally(() => setTreatySaving(false));
    }

    // Treaties: transfer ownership (GM only)
    function handleTransferTreatyOwner(treaty, newOwner) {
        if (!canTransferTreaty(treaty)) return;
        setTreatySaving(true);
        fetch('/api/treaties/transfer', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ id: treaty.id, newOwner })
        })
        .then(res => res.json())
        .then(data => {
            if (data.success) {
                return fetch('/treaties.json')
                    .then(res => res.json())
                    .then(data2 => setTreaties(Array.isArray(data2) ? data2 : []));
            }
        })
        .finally(() => setTreatySaving(false));
    }

    // Treaties: form field change
    function handleTreatyFormChange(e) {
        const { name, value, type, options } = e.target;
        if (type === 'select-multiple') {
            const vals = Array.from(options).filter(o => o.selected).map(o => o.value);
            setTreatyForm(f => ({ ...f, [name]: vals }));
        } else {
            setTreatyForm(f => ({ ...f, [name]: value }));
        }
    }

    // Treaties: render
    function renderTreatyForm() {
        if (!treatyForm) return null;
        return (
            <form className="treaty-form card" onSubmit={handleSaveTreaty}>
                <div className="treaty-form-header">
                    <input
                        name="title"
                        placeholder="Treaty Title"
                        value={treatyForm.title}
                        onChange={handleTreatyFormChange}
                        className="login-input"
                        required
                        disabled={treatySaving}
                    />
                    <select
                        name="status"
                        value={treatyForm.status}
                        onChange={handleTreatyFormChange}
                        className="login-input"
                        disabled={treatySaving}
                    >
                        {TREATY_STATUSES.map(s => (
                            <option key={s.value} value={s.value}>{s.label}</option>
                        ))}
                    </select>
                </div>
                <div className="treaty-form-participants">
                    <div className="participants-select">
                        <label className="participants-label">Participants</label>
                        <select
                            name="participants"
                            value={treatyForm.participants}
                            onChange={handleTreatyFormChange}
                            className="login-input"
                            multiple
                            disabled={treatySaving}
                        >
                            {getEmpireNames().map(e => (
                                <option key={e} value={e}>{e}</option>
                            ))}
                        </select>
                    </div>
                    {account && account.username === "GameMaster" && (
                        <div className="owner-select">
                            <label className="owner-label">Owner:</label>
                            <select
                                name="owner"
                                value={treatyForm.owner}
                                onChange={handleTreatyFormChange}
                                className="login-input"
                                disabled={treatySaving}
                            >
                                {Object.keys(accounts).map(acc => (
                                    <option key={acc} value={acc}>{acc}</option>
                                ))}
                            </select>
                        </div>
                    )}
                </div>
                <label className="treaty-content-label">Treaty Content</label>
                <textarea
                    name="content"
                    placeholder="Treaty Content"
                    value={treatyForm.content}
                    onChange={handleTreatyFormChange}
                    className="login-input"
                    rows={9}
                    required
                    disabled={treatySaving}
                />
                <div className="treaty-form-actions">
                    <button type="submit" className="empire-save-btn" disabled={treatySaving}>
                        {treatyEditId ? "Save Changes" : "Create Treaty"}
                    </button>
                    <button type="button" className="empire-back-btn" onClick={handleCancelTreatyForm} disabled={treatySaving}>Cancel</button>
                    {treatyError && <div className="login-error treaty-error">{treatyError}</div>}
                </div>
            </form>
        );
    }

    function renderTreatyView(treaty) {
        const statusObj = TREATY_STATUSES.find(s => s.value === treaty.status);
        return (
            <div className="treaty-view card">
                <div className="treaty-view-header">
                    <h2 className="treaty-title">{treaty.title}</h2>
                    <span className="treaty-status" style={{color: statusObj.color}}>{statusObj ? statusObj.label : treaty.status}</span>
                </div>
                <div className="treaty-view-owner">
                    Owner: {treaty.owner}
                </div>
                <div className="treaty-view-participants">
                    <b>Participants:</b> {treaty.participants && treaty.participants.length > 0 ? treaty.participants.join(', ') : <i>None</i>}
                </div>
                <div className="treaty-content-static">
                    {treaty.content}
                </div>
                <div className="treaty-view-actions">
                    <button className="empire-back-btn" onClick={() => setTreatyViewId(null)}>Back</button>
                    {canEditTreaty(treaty) && (
                        <button className="empire-save-btn" onClick={() => handleEditTreaty(treaty)}>Edit</button>
                    )}
                    {canTransferTreaty(treaty) && (
                        <select
                            value={treaty.owner}
                            onChange={e => handleTransferTreatyOwner(treaty, e.target.value)}
                            className="owner-transfer-select"
                        >
                            {Object.keys(accounts).map(acc => (
                                <option key={acc} value={acc}>{acc}</option>
                            ))}
                        </select>
                    )}
                </div>
            </div>
        );
    }

    // Add tab for GameMaster
    const showAccountsTab = account && account.username === "GameMaster";
    const tabs = [
        { key: 'channels', label: 'Channels' },
        { key: 'empires', label: 'Empires' },
        { key: 'treaties', label: 'Treaties' }
    ];
    if (showAccountsTab) {
        tabs.push({ key: 'accounts', label: 'Manage Accounts' });
        tabs.push({ key: 'manage-empires', label: 'Manage Empires' });
    }

    // Ref for account edit panel
    const accountEditPanelRef = useRef(null);

    // Scroll to edit panel when editAccount changes
    useEffect(() => {
        if (editAccount && accountEditPanelRef.current) {
            accountEditPanelRef.current.scrollIntoView({ behavior: 'smooth', block: 'center' });
        }
    }, [editAccount]);

    // --- MODIFIED: Only allow delete if GM and permission enabled ---
    function handleDeleteMessage(boardKey, msgIdx) {
        if (!account || account.username !== "GameMaster" || !gmPermissions.canDeleteMessages) return;
        if (!window.confirm("Delete this message?")) return;
        fetch('/api/messages/delete', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ board: boardKey, index: Number(msgIdx) })
        })
        .then(res => res.json())
        .then(() => {
            fetch(`/api/messages?board=${encodeURIComponent(boardKey)}`)
                .then(res => res.json())
                .then(data => {
                    setMessages(prev => ({
                        ...prev,
                        [boardKey]: data
                    }));
                });
        });
    }

    // --- NEW: Show Password toggle ---
    const [showLoginPass, setShowLoginPass] = useState(false);
    const [showAccountPass, setShowAccountPass] = useState(false);
    const [showEditAccountPass, setShowEditAccountPass] = useState(false);

    return (
        <div className="app-container">
            <header className="header">
                <h1>Stellaris RP Diplomatic Message Boards</h1>
                <div className="tabs-bar">
                    {tabs.map(tab => (
                        <button
                            key={tab.key}
                            className={`tab-btn${activeTab === tab.key ? ' active' : ''}`}
                            onClick={() => {
                                setActiveTab(tab.key);
                                setEmpirePage(null);
                                setEditEmpire(null);
                                setEditAccount(null);
                                setTreatyEditId(null);
                                setTreatyViewId(null);
                                setShowTreatyForm(false);
                            }}
                        >
                            {tab.label}
                        </button>
                    ))}
                    {/* --- Hamburger menu at top right --- */}
                    <div className="menu-dropdown-container">
                        <button
                            className="menu-btn"
                            aria-label="Open menu"
                            onClick={handleMenuToggle}
                        >
                            <span className="menu-icon">&#9776;</span>
                        </button>
                        {showMenu && (
                            <div
                                className="menu-dropdown"
                                ref={menuDropdownRef}
                            >
                                <button className="menu-dropdown-item logout-btn" onClick={handleLogout}>
                                    <span className="logout-icon">&#x1F511;</span> Logout
                                </button>
                                <button className="menu-dropdown-item" onClick={handleShowAbout}>
                                    About
                                </button>
                                <button className="menu-dropdown-item" onClick={handleShowTheme}>
                                    Theme Switcher
                                </button>
                                {account && account.username === "GameMaster" && (
                                    <button className="menu-dropdown-item" onClick={handlePermissionsToggle}>
                                        Manage Permissions
                                    </button>
                                )}
                            </div>
                        )}
                        {showPermissions && (
                            <div
                                className="menu-permissions-dropdown"
                                ref={permissionsDropdownRef}
                            >
                                <div className="menu-permissions-title">GameMaster Permissions</div>
                                <label className="menu-permissions-option">
                                    <input
                                        type="checkbox"
                                        checked={!!gmPermissions.canDeleteMessages}
                                        onChange={e => handlePermissionChange('canDeleteMessages', e.target.checked)}
                                    />
                                    Allow deleting messages
                                </label>
                                {/* Add more GM toggles here if needed */}
                            </div>
                        )}
                    </div>
                </div>
            </header>
            <main className="main-content">
                {activeTab === 'treaties' ? (
                    <section className="treaties-section card">
                        <div className="treaties-header">
                            <h2>Treaties, Contracts & Agreements</h2>
                            {account && (
                                <button
                                    className="empire-save-btn"
                                    onClick={() => openTreatyDialog('new')}
                                >
                                    + Add New Treaty
                                </button>
                            )}
                        </div>
                        {/* Dialogs */}
                        {treatyDialog.open && treatyDialog.mode !== 'view' && (
                            <TreatyDialog
                                open={treatyDialog.open}
                                mode={treatyDialog.mode}
                                data={treatyDialog.data}
                                onSave={handleTreatySave}
                                onClose={closeTreatyDialog}
                                error={treatyError}
                                saving={treatySaving}
                                empires={empires.map(e => e.name)}
                                accounts={accounts}
                            />
                        )}
                        {treatyDialog.open && treatyDialog.mode === 'view' && (
                            <TreatyView
                                treaty={treatyDialog.data}
                                onBack={closeTreatyDialog}
                                onEdit={() => openTreatyDialog('edit', treatyDialog.data)}
                                onTransfer={newOwner => handleTreatyTransferOwner(treatyDialog.data.id, newOwner)}
                                canEdit={canEditTreaty(treatyDialog.data)}
                                canTransfer={canTransferTreaty(treatyDialog.data)}
                                accounts={accounts}
                            />
                        )}
                        {/* List */}
                        <div className="treaties-list-container">
                            {!treatiesLoaded && <div className="login-loading">Loading treaties...</div>}
                            {treaties.length === 0 && treatiesLoaded && (
                                <div className="no-messages">No treaties yet.</div>
                            )}
                            <ul className="treaties-list">
                                {treaties.map(treaty => (
                                    <li
                                        key={treaty.id}
                                        className="treaty-item"
                                        onClick={() => setTreatyDialog({ open: true, mode: 'view', data: treaty })}
                                    >
                                        <div className="treaty-info">
                                            <div className="treaty-title">{treaty.title}</div>
                                            <div className="treaty-meta">
                                                <div className="treaty-owner">Owner: {treaty.owner}</div>
                                                <div className="treaty-participants">
                                                    <b>Participants:</b> {treaty.participants?.join(', ') || <i>None</i>}
                                                </div>
                                            </div>
                                            <div className="treaty-content">
                                                {treaty.content.length > 180 ? treaty.content.slice(0,180) + 'â€¦' : treaty.content}
                                            </div>
                                        </div>
                                        <div className="treaty-actions">
                                            <span className="treaty-status">
                                                {({
                                                    discussion: 'ðŸ—¨ï¸ In Discussion',
                                                    active: 'âœ… Active',
                                                    broken: 'âŒ Broken',
                                                    expired: 'âŒ› Expired',
                                                    other: 'â“ Other'
                                                })[treaty.status] || treaty.status}
                                            </span>
                                            <span className="treaty-buttons">
                                                <button
                                                    className="empire-view-btn"
                                                    onClick={ev => { ev.stopPropagation(); setTreatyDialog({ open: true, mode: 'view', data: treaty }); }}
                                                >View</button>
                                                {canEditTreaty(treaty) && (
                                                    <button
                                                        className="empire-save-btn"
                                                        onClick={ev => { ev.stopPropagation(); openTreatyDialog('edit', treaty); }}
                                                    >Edit</button>
                                                )}
                                                <button
                                                    className="empire-save-btn"
                                                    style={{background: '#555'}}
                                                    onClick={ev => { ev.stopPropagation(); handleCopyTreaty(treaty); }}
                                                    title="Copy treaty"
                                                >Copy</button>
                                            </span>
                                        </div>
                                    </li>
                                ))}
                            </ul>
                        </div>
                    </section>
                ) : activeTab === 'manage-empires' && showAccountsTab ? (
                    <section className="account-manage-section card">
                        <h2>Manage Empires</h2>
                        <form onSubmit={handleCreateEmpire} className="account-form">
                            <div className="account-form-fields">
                                <input
                                    placeholder="New empire name"
                                    value={newEmpireName}
                                    onChange={e => setNewEmpireName(e.target.value)}
                                    required
                                    className="login-input"
                                    disabled={newEmpireLoading}
                                />
                                <button type="submit" className="login-btn" disabled={newEmpireLoading}>Create Empire</button>
                            </div>
                        </form>
                        {newEmpireError && <div className="login-error">{newEmpireError}</div>}
                        <h3>Empires & Account Assignment</h3>
                        <ul className="accounts-list">
                            {empires.map(emp => (
                                <li
                                    key={emp.name}
                                    className="account-item"
                                >
                                    <b>{emp.name}</b>
                                    <span className="account-actions">
                                        <select
                                            value={emp.account || ''}
                                            onChange={e => {
                                                const val = e.target.value;
                                                if (val) handleLinkAccount(emp.name, val);
                                                else handleUnlinkAccount(emp.name);
                                            }}
                                            className="account-select"
                                        >
                                            <option value="" className="account-option">Select account...</option>
                                            {Object.keys(accounts).map(accName => (
                                                <option key={accName} value={accName} className="account-option">
                                                    {accName}
                                                </option>
                                            ))}
                                        </select>
                                        <button
                                            className="account-manage-btn account-delete-btn"
                                            onClick={() => handleDeleteEmpire(emp.name)}
                                            disabled={newEmpireLoading}
                                        >Delete</button>
                                    </span>
                                </li>
                            ))}
                        </ul>
                    </section>
                ) : activeTab === 'accounts' && showAccountsTab ? (
                    <section className="account-manage-section card">
                        <h2>Manage Accounts</h2>
                        <form onSubmit={handleCreateAccount} className="account-form">
                            <div className="account-form-fields">
                                <input
                                    placeholder="Account name"
                                    value={newAccountName}
                                    onChange={e => setNewAccountName(e.target.value)}
                                    required
                                    className="login-input"
                                    disabled={accountsTabLoading}
                                />
                                <input
                                    type="password"
                                    placeholder="Password"
                                    value={newAccountPass}
                                    onChange={e => setNewAccountPass(e.target.value)}
                                    required
                                    className="login-input"
                                    disabled={accountsTabLoading}
                                />
                                <button type="submit" className="login-btn" disabled={accountsTabLoading}>Create</button>
                            </div>
                        </form>
                        {accountError && <div className="login-error">{accountError}</div>}
                        {accountSuccess && <div className="login-loading" style={{color:'limegreen'}}>{accountSuccess}</div>}

                        {/* Restore account list with Edit/Delete buttons */}
                        <h3>All Accounts</h3>
                        <ul className="accounts-list">
                            {Object.keys(accounts).map(acc => (
                                <li
                                    key={acc}
                                    className="account-item"
                                >
                                    <b>{acc}</b>
                                    {acc !== "GameMaster" && (
                                        <span className="account-actions">
                                            <button
                                                className="account-manage-btn account-edit-btn"
                                                onClick={() => {
                                                    setEditAccount(acc);
                                                    setEditAccountName(acc);
                                                    setEditAccountPass('');
                                                    setEditAccountError('');
                                                }}
                                                disabled={editAccountLoading}
                                            >Edit</button>
                                            <button
                                                className="account-manage-btn account-delete-btn"
                                                onClick={() => handleDeleteAccount(acc)}
                                                disabled={editAccountLoading}
                                            >Delete</button>
                                        </span>
                                    )}
                                </li>
                            ))}
                        </ul>
                        {editAccount && (
                            <div
                                className="account-edit-panel card"
                                ref={accountEditPanelRef}
                            >
                                <h3>Edit Account: <b>{editAccount}</b></h3>
                                <form onSubmit={handleRenameAccount} className="edit-account-form">
                                    <label>
                                        Rename to:
                                        <input
                                            value={editAccountName}
                                            onChange={e => setEditAccountName(e.target.value)}
                                            className="login-input"
                                            disabled={editAccountLoading}
                                        />
                                    </label>
                                    <button type="submit" className="empire-save-btn" disabled={editAccountLoading}>Rename</button>
                                </form>
                                <form onSubmit={handleChangePassword} className="change-password-form">
                                    <label>
                                        New Password:
                                        <input
                                            type="password"
                                            value={editAccountPass}
                                            onChange={e => setEditAccountPass(e.target.value)}
                                            className="login-input"
                                            disabled={editAccountLoading}
                                        />
                                    </label>
                                    <button type="submit" className="empire-save-btn" disabled={editAccountLoading}>Change Password</button>
                                </form>
                                {editAccountError && <div className="login-error">{editAccountError}</div>}
                                <button className="empire-back-btn" onClick={() => setEditAccount(null)} disabled={editAccountLoading}>Cancel</button>
                            </div>
                        )}
                    </section>
                ) : activeTab === 'empires' ? (
                    editEmpire ? (
                        <section className="empires-info-section card">
                            <h2>Edit {editEmpire}</h2>
                            <EmpireEditPanel name={editEmpire} onCancel={() => setEditEmpire(null)} />
                        </section>
                    ) : empirePage ? (
                        <EmpirePanel name={empirePage} canEdit={account && account.username === empirePage} />
                    ) : (
                        <section className="empires-info-section card">
                            <div style={{
                                display: 'flex',
                                flexDirection: 'column',
                                alignItems: 'center',
                                marginBottom: '1.5rem',
                                gap: '0.3rem'
                            }}>
                                <h2 className="section-title" style={{margin: 0, textAlign: 'center'}}>Empires Overview</h2>
                                <span style={{
                                    color: '#aaa',
                                    fontSize: '1.05rem',
                                    fontWeight: 500,
                                    textAlign: 'center'
                                }}>
                                    Total Empires: <b>{getEmpireNames().length}</b>
                                </span>
                                <input
                                    type="text"
                                    placeholder="Search empires..."
                                    value={empireSearch}
                                    onChange={e => setEmpireSearch(e.target.value)}
                                    style={{
                                        marginTop: '0.7em',
                                        padding: '0.5em 1em',
                                        borderRadius: 8,
                                        border: '1px solid var(--border)',
                                        background: 'var(--primary-bg)',
                                        color: 'var(--text)',
                                        fontSize: '1em',
                                        maxWidth: 320
                                    }}
                                />
                            </div>
                            <div className="empires-info-list" style={{display: 'flex', flexWrap: 'wrap', gap: '1.5rem', justifyContent: 'flex-start'}}>
                                {getEmpireNames().map(e => {
                                    const linkedAccount = getEmpireAccount(e);
                                    const canEdit = account && (account.username === linkedAccount || account.username === "GameMaster");
                                    return (
                                        <div
                                            key={e}
                                            className="empire-info-card empire-list-card"
                                            style={{
                                                minWidth: 260,
                                                maxWidth: 340,
                                                flex: '1 1 320px',
                                                background: 'var(--primary-bg)',
                                                border: '1.5px solid var(--accent)',
                                                borderRadius: 12,
                                                boxShadow: '0 2px 12px #0002',
                                                padding: '1.2rem 1.2rem',
                                                display: 'flex',
                                                flexDirection: 'column',
                                                gap: '0.7rem',
                                                cursor: 'pointer'
                                            }}
                                            onClick={() => setEmpirePage(e)}
                                        >
                                            <h3 className="empire-info-title" style={{margin: 0, color: 'var(--accent)', textAlign: 'center'}}>{e}</h3>
                                            <div style={{color: '#b3cfff', fontWeight: 500, fontSize: '1.05em', textAlign: 'center', marginBottom: '0.5rem'}}>
                                                {linkedAccount ? <span title="Assigned Account">{linkedAccount}</span> : <i>Unassigned</i>}
                                            </div>
                                            <div className="empire-actions" style={{display: 'flex', gap: '0.5rem', marginTop: '0.5rem', justifyContent: 'center'}}>
                                                <button
                                                    className="empire-view-btn"
                                                    style={{minWidth: 80}}
                                                    onClick={ev => {ev.stopPropagation(); setEmpirePage(e);}}
                                                >View</button>
                                                {canEdit && (
                                                    <button
                                                        className="empire-save-btn"
                                                        style={{minWidth: 80}}
                                                        onClick={ev => {ev.stopPropagation(); setEditEmpire(e);}}
                                                    >Edit</button>
                                                )}
                                            </div>
                                        </div>
                                    );
                                })}
                            </div>
                        </section>
                    )
                ) : (
                    <>
                        {!account ? (
                            <section className="account-section card">
                                <h2 className="login-title">Login</h2>
                                <form onSubmit={handleLogin} className="account-form login-form">
                                    <div className="login-fields">
                                        <input
                                            placeholder="Empire name"
                                            value={loginUser}
                                            onChange={e => setLoginUser(e.target.value)}
                                            required
                                            disabled={!accountsLoaded}
                                            className="login-input"
                                        />
                                        <div style={{position: 'relative'}}>
                                            <input
                                                type={showLoginPass ? "text" : "password"}
                                                placeholder="Password"
                                                value={loginPass}
                                                onChange={e => setLoginPass(e.target.value)}
                                                required
                                                disabled={!accountsLoaded}
                                                className="login-input"
                                            />
                                            <button
                                                type="button"
                                                style={{
                                                    position: 'absolute',
                                                    right: 8,
                                                    top: 8,
                                                    background: 'none',
                                                    border: 'none',
                                                    color: '#aaa',
                                                    cursor: 'pointer'
                                                }}
                                                tabIndex={-1}
                                                onClick={() => setShowLoginPass(v => !v)}
                                            >
                                                {showLoginPass ? 'ðŸ™ˆ' : 'ðŸ‘ï¸'}
                                            </button>
                                        </div>
                                    </div>
                                    <button type="submit" disabled={!accountsLoaded} className="login-btn">Login</button>
                                </form>
                                {!accountsLoaded && <div className="login-loading">Loading accounts...</div>}
                                {error && <div className="login-error">{error}</div>}
                            </section>
                        ) : (
                            // Center the channels page
                            <div style={{ display: 'flex', justifyContent: 'center', width: '100%' }}>
                                <section className="boards-section">
                                    {/* Logout button at top right */}
                                    {/* <div className="channels-header">
                                        <span style={{marginRight: 'auto', color: '#aaa', fontWeight: 500}}>
                                            Logged in as <b>{account.username}</b>
                                        </span>
                                        <button
                                            className="logout-btn"
                                            onClick={handleLogout}
                                            title="Logout"
                                        >
                                            <span className="logout-icon">&#x1F511;</span> Logout
                                        </button>
                                    </div> */}
                                    {/* Main area: boards/channels list left, text channel right */}
                                    <div className="channels-main">
                                        <div className="channels-sidebar">
                                            <div className="boards-list card">
                                                <h2>
                                                    {account && account.username === "GameMaster"
                                                        ? "All Boards"
                                                        : "Your Diplomatic Channels"}
                                                </h2>
                                                <div className="boards-list-header">
                                                    <span className="boards-list-count">
                                                        {filteredBoards.length} {filteredBoards.length === 1 ? "channel" : "channels"}
                                                    </span>
                                                </div>
                                                <ul className="boards-list-ul">
                                                    {filteredBoards.length === 0 && (
                                                        <li className="no-channels">No channels available.</li>
                                                    )}
                                                    {filteredBoards.map(([a, b]) => {
                                                        const key = a + '|' + b;
                                                        return (
                                                            <li key={key} className={`boards-list-item${selected === key ? ' active' : ''}`}>
                                                                <button
                                                                    className="board-btn"
                                                                    onClick={() => handleBoardSelect([a, b])}
                                                                >
                                                                    <span className="board-nations">{a}</span>
                                                                    <span className="board-arrow">â†”</span>
                                                                    <span className="board-nations">{b}</span>
                                                                </button>
                                                            </li>
                                                        );
                                                    })}
                                                </ul>
                                            </div>
                                        </div>
                                        <div className="channels-content">
                                            <div className="board-messages card">
                                                {(selected && filteredBoards.length > 0) ? (
                                                    <div className="board-messages-inner">
                                                        <div className="board-title-row">
                                                            <h2 className="board-title">{selected.replace('|', ' â†” ')}</h2>
                                                        </div>
                                                        <div className="messages-panel">
                                                            <div className="messages-list-container">
                                                                <ul className="messages-list">
                                                                    {(messages[selected] || []).length === 0 && <li className="no-messages">No messages yet.</li>}
                                                                    {(messages[selected] || []).map((msg, i) => {
                                                                        const [partnerA, partnerB] = selected.split('|');
                                                                        let partnerClass = '';
                                                                        if (msg.author === partnerA) partnerClass = 'message-partnerA';
                                                                        else if (msg.author === partnerB) partnerClass = 'message-partnerB';
                                                                        return (
                                                                            <li
                                                                                key={i}
                                                                                className={`message-item ${partnerClass}`}
                                                                                style={{
                                                                                    display: 'flex',
                                                                                    alignItems: 'stretch',
                                                                                    justifyContent: 'flex-start',
                                                                                    gap: '0.7rem'
                                                                                }}
                                                                            >
                                                                                <div style={{
                                                                                    flex: 1,
                                                                                    minWidth: 0,
                                                                                    display: 'flex',
                                                                                    flexDirection: 'column',
                                                                                    gap: '0.15rem',
                                                                                    justifyContent: 'center'
                                                                                }}>
                                                                                    <div className="message-meta" style={{marginBottom: 0}}>
                                                                                        <span className="message-author">{msg.author}</span>
                                                                                        <small className="message-date">{new Date(msg.timestamp).toLocaleString()}</small>
                                                                                    </div>
                                                                                    <div className="message-text">{msg.text}</div>
                                                                                </div>
                                                                                {account && account.username === "GameMaster" && gmPermissions.canDeleteMessages && (
                                                                                    <div style={{
                                                                                        display: 'flex',
                                                                                        alignItems: 'center',
                                                                                        justifyContent: 'center',
                                                                                        minWidth: '2.5em',
                                                                                        paddingLeft: '0.5em'
                                                                                    }}>
                                                                                        <button
                                                                                            title="Delete message"
                                                                                            style={{
                                                                                                background: 'none',
                                                                                                border: 'none',
                                                                                                color: '#ff4d4d',
                                                                                                cursor: 'pointer',
                                                                                                fontSize: '1.3em',
                                                                                                display: 'flex',
                                                                                                alignItems: 'center',
                                                                                                height: '2.2em'
                                                                                            }}
                                                                                            onClick={e => {
                                                                                                e.stopPropagation();
                                                                                                handleDeleteMessage(selected, i);
                                                                                            }}
                                                                                        >
                                                                                            <span role="img" aria-label="Delete">&#128465;</span>
                                                                                        </button>
                                                                                    </div>
                                                                                )}
                                                                            </li>
                                                                        );
                                                                    })}
                                                                    <div ref={messagesEndRef} />
                                                                </ul>
                                                            </div>
                                                            <form onSubmit={postMessage} className="message-form message-form-bottom">
                                                                <input
                                                                    placeholder="Your empire"
                                                                    value={account.username}
                                                                    disabled
                                                                    required
                                                                    className="author-input"
                                                                />
                                                                <input
                                                                    placeholder="Message"
                                                                    value={text}
                                                                    onChange={e => setText(e.target.value)}
                                                                    required
                                                                    className="text-input"
                                                                />
                                                                <button type="submit" className="send-btn">Send</button>
                                                            </form>
                                                        </div>
                                                    </div>
                                                ) : (
                                                    <div className="no-messages">Select a channel to view messages.</div>
                                                )}
                                            </div>
                                        </div>
                                    </div>
                                </section>
                            </div>
                        )}
                    </>
                )}
            </main>
            <footer className="footer">
                <span>Stellaris RP Board &copy; {new Date().getFullYear()}</span>
            </footer>
        </div>
    );
}

export default App;
