// Account handlers
export function handleRenameAccount(e, editAccount, editAccountName, setEditAccountLoading, setEditAccountError, setAccounts, setEditAccount, setAccountSuccess) {
    e.preventDefault();
    setEditAccountLoading(true);
    setEditAccountError('');
    if (!editAccountName.trim()) {
        setEditAccountError('Account name is required.');
        setEditAccountLoading(false);
        return;
    }
    fetch('/api/accounts/rename', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ oldName: editAccount, newName: editAccountName.trim() })
    })
    .then(res => res.json())
    .then(data => {
        if (data.success) {
            setAccounts(prev => {
                const updated = { ...prev };
                updated[editAccountName.trim()] = updated[editAccount];
                delete updated[editAccount];
                return updated;
            });
            setEditAccount(null);
            setAccountSuccess('Account renamed successfully.');
        } else {
            setEditAccountError(data.error || 'Failed to rename account.');
        }
    })
    .catch(() => setEditAccountError('Failed to rename account.'))
    .finally(() => setEditAccountLoading(false));
}

export function handleChangePassword(e, editAccount, editAccountPass, setEditAccountLoading, setEditAccountError, setAccounts, setEditAccount, setAccountSuccess) {
    e.preventDefault();
    setEditAccountLoading(true);
    setEditAccountError('');
    if (!editAccountPass.trim()) {
        setEditAccountError('Password is required.');
        setEditAccountLoading(false);
        return;
    }
    fetch('/api/accounts/changePassword', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ name: editAccount, pass: editAccountPass.trim() })
    })
    .then(res => res.json())
    .then(data => {
        if (data.success) {
            setAccounts(prev => ({ ...prev, [editAccount]: editAccountPass.trim() }));
            setEditAccount(null);
            setAccountSuccess('Password changed successfully.');
        } else {
            setEditAccountError(data.error || 'Failed to change password.');
        }
    })
    .catch(() => setEditAccountError('Failed to change password.'))
    .finally(() => setEditAccountLoading(false));
}

export function handleDeleteAccount(acc, setEditAccountLoading, setEditAccountError, setAccounts, setAccountSuccess) {
    setEditAccountLoading(true);
    setEditAccountError('');
    if (!window.confirm(`Delete account "${acc}"? This cannot be undone.`)) {
        setEditAccountLoading(false);
        return;
    }
    fetch('/api/accounts/delete', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ name: acc })
    })
    .then(res => res.json())
    .then(data => {
        if (data.success) {
            setAccounts(prev => {
                const updated = { ...prev };
                delete updated[acc];
                return updated;
            });
            setAccountSuccess('Account deleted.');
        } else {
            setEditAccountError(data.error || 'Failed to delete account.');
        }
    })
    .catch(() => setEditAccountError('Failed to delete account.'))
    .finally(() => setEditAccountLoading(false));
}

// Empire handlers
export function handleDeleteEmpire(empireName, setNewEmpireLoading, setNewEmpireError, setEmpires) {
    setNewEmpireLoading(true);
    setNewEmpireError('');
    if (!window.confirm(`Delete empire "${empireName}"? This cannot be undone.`)) {
        setNewEmpireLoading(false);
        return;
    }
    fetch('/api/empires/delete', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ name: empireName })
    })
    .then(res => res.json())
    .then(data => {
        if (data.success) {
            setEmpires(prev => prev.filter(e => e.name !== empireName));
        } else {
            setNewEmpireError(data.error || 'Failed to delete empire.');
        }
    })
    .catch(() => setNewEmpireError('Failed to delete empire.'))
    .finally(() => setNewEmpireLoading(false));
}
