import React from 'react';

export default function SearchSortBar({
    searchValue,
    onSearchChange,
    sortValue,
    onSortChange,
    sortOptions,
    searchPlaceholder
}) {
    return (
        <div className="search-sort-bar" style={{ display: 'flex', gap: '0.7rem', alignItems: 'center', marginBottom: '1.2rem' }}>
            <input
                type="text"
                placeholder={searchPlaceholder || "Search..."}
                value={searchValue}
                onChange={e => onSearchChange(e.target.value)}
                className="treaty-search-input"
                style={{ flex: 1 }}
            />
            {searchValue && (
                <button
                    className="treaty-search-clear-btn"
                    onClick={() => onSearchChange('')}
                    title="Clear search"
                >âœ•</button>
            )}
            <select
                value={sortValue}
                onChange={e => onSortChange(e.target.value)}
                className="treaty-sort-select"
            >
                {sortOptions.map(opt => (
                    <option key={opt.value} value={opt.value}>{opt.label}</option>
                ))}
            </select>
        </div>
    );
}
